import { createRouter, createWebHistory } from 'vue-router'; // 使用 Vue 3 的新导入方式
import CommunityHome from '@/components/CommunityHome.vue';
import UserLogin from '@/components/UserLogin.vue';
import UserRegister from '@/components/UserRegister.vue';
import UserDiscover from '@/components/DiscoverPage.vue';
import UserProfile from '@/components/Profile.vue';
import NewsDetail from '@/components/NewsDetail.vue'; // 导入新闻详情组件
import ArticleEditor from '@/components/ArticleEditor.vue'; // 导入文章编写组件
import CanteenOrder from '@/components/CanteenOrder.vue'; // 导入订餐组件
import CanteenAdmin from '@/components/CanteenAdmin.vue';
import OrderDetail from '@/components/OrderDetail.vue';
import OrderManagement from '@/components/OrderManagement.vue';
import SubmitFeedback from '@/components/SubmitFeedback.vue';
import ManageFeedback from '@/components/ManageFeedback.vue';
import AdminManageVotes from '@/components/AdminManageVotes.vue';
import VoteDetail from '@/components/VoteDetail.vue';
import VoteList from '@/components/VoteList.vue';

// 创建路由
const routes = [
  { path: '/', name: 'CommunityHome', component: CommunityHome },
  { path: '/login', name: 'UserLogin', component: UserLogin },
  { path: '/register', name: 'UserRegister', component: UserRegister },
  { path: '/profile', name: 'Profile', component: UserProfile, meta: { requiresAuth: true } },
  { path: '/discover', name: 'Discover', component: UserDiscover },
  { path: '/news/:id', name: 'NewsDetail', component: NewsDetail }, // 添加新闻详情的路由
  { path: '/articles/new', name: 'ArticleEditor', component: ArticleEditor, meta: { requiresAuth: true } }, // 添加文章编写的路由
  { path: '/canteenorder', name: 'CanteenOrder', component: CanteenOrder, meta: { requiresAuth: true } },
  { path: '/canteenadmin', name: 'CanteenAdmin', component: CanteenAdmin, meta: { requiresAuth: true, requiresAdmin: true } },
  { path: '/order/:orderId', name: 'OrderDetail', component: OrderDetail, meta: { requiresAuth: true }},
  { path: '/canteen/orders', name: 'OrderManagement', component: OrderManagement, meta: { requiresAuth: true, requiresAdmin: true } },
  { path: '/feedback', name: 'SubmitFeedback', component: SubmitFeedback, meta: { requiresAuth: true }},
  { path: '/managefeedback', name: 'ManageFeedback', component: ManageFeedback, meta: { requiresAuth: true, requiresAdmin: true }},
  { path: '/adminvotes', name: 'AdminManageVotes', component: AdminManageVotes, meta: { requiresAuth: true, requiresAdmin: true }},
  { path: '/votedetail/:id', name: 'VoteDetail', component: VoteDetail},
  { path: '/votelist', name: 'VoteList', component: VoteList},
];

// 创建 router 实例
const router = createRouter({
  history: createWebHistory(),
  routes,
});

// 全局路由守卫
router.beforeEach((to, from, next) => {
  const token = localStorage.getItem('token');
  const userRole = localStorage.getItem('userRole');
  const isLoggedIn = token !== null;

  // 需要登录但未登录
  if (to.meta.requiresAuth && !isLoggedIn) {
    next({
      path: '/login',
      query: { redirect: to.fullPath }  // 保存原目标路径
    });
    return;
  }

  // 需要管理员权限但不是管理员
  if (to.meta.requiresAdmin && userRole !== 'admin') {
    next('/');
    return;
  }

  next();
});

export default router;
