import { createRouter, createWebHistory } from 'vue-router';
import CommunityHome from '@/components/CommunityHome.vue';
import UserLogin from '@/components/UserLogin.vue';
import UserRegister from '@/components/UserRegister.vue';
import UserDiscover from '@/components/DiscoverPage.vue';
import UserProfile from '@/components/Profile.vue';
import NewsDetail from '@/components/NewsDetail.vue';
import ArticleEditor from '@/components/ArticleEditor.vue';
import CanteenOrder from '@/components/CanteenOrder.vue';
import CanteenAdmin from '@/components/CanteenAdmin.vue';
import OrderDetail from '@/components/OrderDetail.vue';
import OrderManagement from '@/components/OrderManagement.vue';
import SubmitFeedback from '@/components/SubmitFeedback.vue';
import ManageFeedback from '@/components/ManageFeedback.vue';
import AdminManageVotes from '@/components/AdminManageVotes.vue';
import VoteDetail from '@/components/VoteDetail.vue';
import VoteList from '@/components/VoteList.vue';

const routes = [
  { 
    path: '/', 
    name: 'CommunityHome', 
    component: CommunityHome 
  },
  { 
    path: '/login', 
    name: 'UserLogin', 
    component: UserLogin 
  },
  { 
    path: '/register', 
    name: 'UserRegister', 
    component: UserRegister 
  },
  { 
    path: '/profile', 
    name: 'Profile', 
    component: UserProfile,
    meta: { requiresAuth: true }
  },
  { 
    path: '/discover', 
    name: 'Discover', 
    component: UserDiscover 
  },
  { 
    path: '/news/:id', 
    name: 'NewsDetail', 
    component: NewsDetail 
  },
  { 
    path: '/articles/new', 
    name: 'ArticleEditor', 
    component: ArticleEditor,
    meta: { requiresAuth: true }
  },
  { 
    path: '/canteenorder', 
    name: 'CanteenOrder', 
    component: CanteenOrder,
    meta: { requiresAuth: true }
  },
  { 
    path: '/canteenadmin', 
    name: 'CanteenAdmin', 
    component: CanteenAdmin,
    meta: { requiresAuth: true, requiresAdmin: true }
  },
  { 
    path: '/order/:orderId',
    name: 'OrderDetail',
    component: OrderDetail,
    meta: { requiresAuth: true }
  },
  { 
    path: '/canteen/orders', 
    name: 'OrderManagement', 
    component: OrderManagement,
    meta: { requiresAuth: true, requiresAdmin: true }
  },
  { 
    path: '/feedback', 
    name: 'SubmitFeedback', 
    component: SubmitFeedback,
    meta: { requiresAuth: true }
  },
  { 
    path: '/managefeedback', 
    name: 'ManageFeedback', 
    component: ManageFeedback,
    meta: { requiresAuth: true, requiresAdmin: true }
  },
  { 
    path: '/adminvotes', 
    name: 'AdminManageVotes', 
    component: AdminManageVotes,
    meta: { requiresAuth: true, requiresAdmin: true }
  },
  { 
    path: '/votedetail/:id', 
    name: 'VoteDetail', 
    component: VoteDetail 
  },
  { 
    path: '/votelist', 
    name: 'VoteList', 
    component: VoteList 
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// 全局路由守卫
router.beforeEach((to, from, next) => {
  const isLoggedIn = localStorage.getItem('token') !== null;
  const userRole = localStorage.getItem('userRole');

  if (to.meta.requiresAuth && !isLoggedIn) {
    // 需要登录但未登录，重定向到登录页
    next({
      path: '/login',
      query: { redirect: to.fullPath }
    });
  } else if (to.meta.requiresAdmin && userRole !== 'admin') {
    // 需要管理员权限但不是管理员，重定向到首页
    next({ path: '/' });
  } else {
    next();
  }
});

export default router;
