import axios from 'axios';
const API = process.env.VUE_APP_BASE_URL;
export default {
  name: 'VoteList',
  data() {
    return {
      votes: [],
      loading: false
    };
  },
  mounted() {
    this.fetchVotes();
  },
  methods: {
    async fetchVotes() {
      this.loading = true;
      try {
        const response = await axios.get(`${API}/api/canteen/votes`);
        this.votes = response.data;
      } catch (error) {
        console.error('获取投票失败:', error);
      } finally {
        this.loading = false;
      }
    }
  }
};
